import React, {useState, useEffect} from 'react'
import {Link} from 'react-router-dom';
import {AppBar, Toolbar, Grid, Card, CircularProgress, CardMedia, CardContent, Typography} from "@material-ui/core";
import {makeStyles} from "@material-ui/core/styles";

const API_URL = `http://localhost:8080/api/v1/amusement-parks/?amusement-park-name=${match.params.amusementParkName}`

const useStyles = makeStyles({
    RollerCoasterContainer: {
        paddingTop: '20px',
        paddingLeft: '50px',
        paddingRight: '50px'
    }
})


const RollerCoasterList = ( {match} ) => {
    const classes = useStyles()
    const [RollerCoasters, setRollerCoasters] = useState([]);

    useEffect(() => {
        fetchRollerCoasters();
    }, [])

    const fetchRollerCoasters = async () => {
        const data = await fetch(API_URL);
        const RollerCoasters = await data.json();
        console.log(RollerCoasters);
        setRollerCoasters(RollerCoasters);
    }


    const getRollerCoasterCard = (rollerCoasterID) => {
        const { rollerCoasterName, Votes} = RollerCoasters[`${rollerCoasterID}`]
        return(
            <Grid item xs={12} sm={4} key={rollerCoasterID}>
                <Card>
                    <CardMedia
                        style={{ width: "130px", height: "130px"}}
                    />
                    <CardContent>
                        <Typography>{`${rollerCoasterName}`}</Typography>
                    </CardContent>
                </Card>
            </Grid>
        )
    }



    return (
        <>
            <AppBar position="static">
                <Toolbar />
            </AppBar>
            {RollerCoasters ? (
            <Grid container spacing={5} className={classes.RollerCoasterContainer}>
                {Object.keys(RollerCoasters).map(
                    (rollerCoasterName) =>
                    getRollerCoasterCard(rollerCoasterName)
            )}
            </Grid>
            ) : (
                <CircularProgress/>
            )}
        </>
    )
}

export default RollerCoasterList
